CONNECTOR.ATTRIBUTE:
GRAPHREP rounded:0.1cm
PEN w:0.05cm 
EDGE

AVAL sModelType: "ModelTypeExp"

START

IF ((sModelType = "Business process diagram (BPMN 2.0)") OR (sModelType = "Choreography diagram (BPMN 2.0)"))
{
  AVAL sStandard: "Default"
  IF (sStandard = "Yes")
  {
    LINE x1:-0.7cm y1:-0.1cm x2:-0.5cm y2:0.1cm
  }

  AVAL condExpression: "Transition condition"
  AVAL fromType: "FromType"
  IF ((condExpression != "") AND ((fromType = "Task") OR (fromType = "Sub-Process")))
  {
    FILL color:white
    POLYGON 4 x1:0.0cm y1:0.0cm x2:-0.15cm y2:-0.15cm x3:-0.3cm y3:0.0cm x4:-0.15cm y4:0.15cm
  }
}

END	 


IF ((sModelType = "Business process diagram (BPMN 2.0)") OR (sModelType = "Choreography diagram (BPMN 2.0)"))
    FILL color:black
    POLYGON 3 x1:-.33cm y1:-.11cm x2:0.0cm y2:0.0cm x3:-.33cm y3:0.11cm
ELSE
POLYLINE 3 x1:-.3cm y1:.1cm x2:0cm y2:0cm x3:-.3cm y3:-.1cm
ENDIF

MIDDLE
AVAL s:"Rotate the visualised values by 90 degrees"
AVAL v:"Visualized values"
AVAL d:"Representation"
AVAL sp:"Language"
AVAL cl:"Font colour"

SET maxTxtWidth:(4cm)

  AVAL name:"Denomination"
  AVAL transCond:"Transition condition"

AVAL transVal:"Transition probability"
IF (v = "Denomination")
{
  SET txt1:(name)
  SET txt2:("")
}
ELSIF (v = "Transition condition")
{
  SET txt1:(transCond)
  SET txt2:("")
}
ELSIF (v = "Transition probability")
{
  SET txt1:("")
  SET txt2:(transVal)
}
ELSIF (v = "Transition probability and transition condition")
{
  SET txt1:(transCond)
  SET txt2:(transVal)
}
ELSIF (v = "Transition probability and denomination")
{
  SET txt1:(name)
  SET txt2:(transVal)
}
ELSE
{
  SET txt1:(name)
  SET txt2:(transCond)
}
IF (cl = "black")
{
  IF (s = "Yes")
  {
    FONT color:black line-orientation:90
  }
  ELSE
  {
    FONT color:black line-orientation:0
  }
}
ELSE
{
  IF (s = "Yes")
  {
    FONT color:whitesmoke line-orientation:90
  }
  ELSE
  {
    FONT color:whitesmoke line-orientation:0
  }
}
IF (d = "above/below")
{
  IF (s = "Yes")
  {
    IF (txt1 != "")
    { 
      TEXT (txt1) line-break:rigorous x:0.2cm y:0.0cm w:c:(maxTxtWidth) h:t
    }
    IF (txt2 != "")
    {
      TEXT (txt2) line-break:rigorous x:-0.2cm y:0.0cm w:c:(maxTxtWidth) h:b
    }
  }
    ELSE
  {
    IF (txt1 != "")
    { 
      TEXT (txt1) line-break:rigorous x:0.0cm y:0.2cm w:c:(maxTxtWidth) h:t
    }
    IF (txt2 != "")
    {
      TEXT (txt2) line-break:rigorous x:0.0cm y:-0.2cm w:c:(maxTxtWidth) h:b
    }
  }
}
IF (d = "left/right")
{
  IF (s = "Yes")
  {
    IF (txt1 != "")
    { 
      TEXT (txt1) line-break:rigorous x:0.0cm y:-0.2cm w:l:(maxTxtWidth) h:c
    }
    IF (txt2 != "")
    {
      TEXT (txt2) line-break:rigorous x:0.0cm y:0.2cm w:r:(maxTxtWidth) h:c
    }
  }
    ELSE
  {
    IF (txt1 != "")
    { 
      TEXT (txt1) line-break:rigorous x:0.2cm y:0.0cm w:l:(maxTxtWidth) h:c
    }
    IF (txt2 != "")
    {
      TEXT (txt2) line-break:rigorous x:-0.2cm y:0.0cm w:r:(maxTxtWidth) h:c
    }
  }
}

GRAPHREP rounded:0.1cm
PEN w:0.05cm 
EDGE

