  SET txt2:("")
}
ELSIF (v = "Transition condition")
{
  SET txt1:(transCond)
  SET txt2:("")
}
ELSIF (v = "Transition probability")
{
  SET txt1:("")
  SET txt2:(transVal)
}
ELSIF (v = "Transition probability and transition condition")
{
  SET txt1:(transCond)
  SET txt2:(transVal)
}
